mapper.update:
UPDATE players
SET rating = #{rating}, display_name = #{displayName}
WHERE id = #{id}

UPDATE players
SET rating = #{rating}
WHERE id = #{playerId}

UPDATE players
SET display_name = #{name}, rep = #{rep}
WHERE id = #{playerId}

mapper.select:
SELECT player_id, 1500 + SUM(rating_delta) AS current_rating, COUNT(*) AS num_games_played FROM entries WHERE game_id < #{fromGameId} AND player_id IN
#{id}
GROUP BY player_id;
